mapper.update:
update s_material_storage_bin

    stock_count = stock_count-#{stockCount},
    pre_use_count = pre_use_count+#{stockCount}

where storage_bin_id = #{storageBinId}

mapper.select:
SELECT ord.order_task_detail_id AS "orderTaskDetailId", ord.material_desc AS "materialDesc", ord.material_unit AS "materialUnit", ord.send_number AS "sendNumber", ord.batch_no AS "batchNo", ord.creater_time AS "createrTime" FROM o_order_task_detail ord WHERE ord.order_task_id = #{orderTaskId}
and ord.material_desc like '%${materialDesc}%'
ORDER BY ord.creater_time
