mapper.select:
select u.id, u.user_id, u.user_name, u.login_account, u.user_password, u.phone, u.avatar, u.org_id, u.status
from t_user u left join t_user_organization o on o.user_id=u.id
u.user_id = #{id}
and u.phone = #{phone}
and u.status = #{status}
limit 1

mapper.insert:

	select  replace(uuid(),'-','') from dual

insert into t_user

	id,
	user_name,
	user_password,
	phone,
	org_id,
	avatar,
	status,
	receive_storage_location


	#{id, jdbcType=VARCHAR}
	,#{userName}
	,#{userPassword}
	,#{phone}
	,#{orgId}
	,#{avatar}
	,#{status}
	,#{receiveStorageLocation}

mapper.select:
select u.id,user_password,user_name,phone,o.ORG_NAME as org_id,u.status,

avatar,receive_storage_location from t_user u left join t_warehouse o on u.org_id=o.id  where 1=1
and user_name like '%${userName}%'
and user_password=#{userPassword}
and phone=#{phone}
and (u.org_id=#{orgId} or o.org_name=#{orgId})
and avatar=#{avatar}
and u.status=#{status}
order by ${sortColums}
